const t="matrixDimension",n=`Fa\xE7a um programa que leia uma string de n\xFAmeros como entrada de um usu\xE1rio que
                  representa uma matriz e diga qual dimens\xE3o dela(quantidade de linhas e colunas).As linhas
                  da matriz s\xE3o separadas por um caractere de ponto e v\xEDrgula \u2018;\u2019e as colunas s\xE3o separadas por
                  um espa\xE7o vazio \u2018 \u2019.`;function r(){const a=prompt('Digite a matriz separando colunas com espa\xE7os e linhas por ";": ').toLowerCase().split(";"),s=a.length,o=a[0].split(" ").length;alert(`Essa \xE9 uma matriz de dimens\xE3o ${s} x ${o}`)}export{t as name,n as question,r as solution};
