const t="countWords",n="Dada uma frase de entrada por um usu\xE1rio, fa\xE7a um programa que conte a ocorr\xEAncia de cada palavra nesta frase. O programa n\xE3o deve distinguir letras mai\xFAsculas e min\xFAsculas.";function c(){const o=prompt("Digite uma frase: ").toLowerCase().replace(/[^a-zA-Z\u00C0-\u00FF ]/g,"").split(" "),e={};o.forEach(a=>{a in e?e[a]+=1:e[a]=1});let s="";for(const a in e)s+=` ${a} (${e[a]}),`;s=s.slice(0,-1),alert(s)}export{t as name,n as question,c as solution};
